import React, {useState} from "react";
import { secondsToDuration } from "../utils/duration";
import classNames from "../utils/class-names";

function Duration ({ min, max, label, session }) {
  const decreaseButtonHandler = () => {
    if (periodDuration > min) (setPeriodDuration(periodDuration - 60));
  }
  
  const increaseButtonHandler = () => {
    if (periodDuration < max) (setPeriodDuration(periodDuration + 60));
  };
  
  return (
    <div className="input-group input-group-lg mb-2">
      <span className="input-group-text" data-testid="duration-focus">
        {/* TODO: Update this text to display the current focus session duration */}
        {`${label} ${secondsToDuration(periodDuration)}`}
      </span>
      <div className="input-group-append">
        {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
        <button
          type="button"
          className={classNames({
            "btn": true,
            "btn-primary": !activeSession,
            "btn-secondary": activeSession,
            "disabled": !activeSession,
          })}
          onClick={decreaseButtonHandler}
          data-testid="decrease-focus"
        >
          <span className="oi oi-minus" />
        </button>
        {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
        <button
          type="button"
          className={classNames({
            "btn": true,
            "btn-primary": !activeSession,
            "btn-secondary": activeSession,
            "disabled": !activeSession,
          })}
          onClick={increaseButtonHandler}
          data-testid="increase-focus"
        >
          <span className="oi oi-plus" />
        </button>
      </div>
    </div>
  );
}

export default Duration;
