import React, { useState } from "react";

function ProgressBar ({ session }) {


  return (
    <div className="progress" style={{ height: "20px" }}>
      <div
        className="progress-bar"
        role="progressbar"
        aria-valuemin="0"
        aria-valuemax="100"
        aria-valuenow={ (session.focusDuration - session.timer) / session.focusDuration * 100 }// TODO: Increase aria-valuenow as elapsed time increases
        style={{ width:`${ (session.focusDuration - session.timer) / session.focusDuration * 100}%` }} // TODO: Increase width % as elapsed time increases
      />
  </div>
  );
}

export default ProgressBar;
