import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import FocusDuration from "./FocusDuration";
import BreakDuration from "./BreakDuration";
import Countdown from "./Countdown";
// import the formatting utilities for duration
import { minutesToDuration, secondsToDuration } from "../utils/duration"

function changeSessionType(session, setSession) {

  if (session.focusSession) {
    // do this when changing from focus to break
    setSession({
      ...session,
      focusSession: false,
      timer: session.breakDuration,
    });
  } else {
    // do this when changing from break to focus
    setSession({
      ...session,
      focusSession: true,
      timer: session.focusDuration,
    });
  }
}

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  const sessionInitialState = {
    active: false,
    focusSession: true,
    focusDuration: 10,  // 1500
    breakDuration: 5,   // 300
    timer: 1500,
    progressBar: 0,
  }

  // set up state variable for whether there is an active session
  const [session, setSession] = useState({...sessionInitialState});

console.log(session.progressBar)

  useInterval(
    () => {
      if (session.focusSession){
        setSession({
          ...session,
          timer: session.timer - 1,
          progressBar: (session.focusDuration - session.timer) / session.focusDuration * 100,
        });
      } else {
        setSession({
          ...session,
          timer: session.timer - 1,
          progressBar: (session.breakDuration - session.timer) / session.breakDuration * 100,
        });
      }

      if (session.timer < 1) (changeSessionType(session, setSession));

    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);

    if (!session.active) {
      setSession({
        ...session,
        active: true,
        timer: session.focusDuration,
      });
    }
  }

  const stopButtonHandler = () => {
    setIsTimerRunning(false);
    setSession({
      ...session,
      active: false,
      focusSession: true,
    });
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <FocusDuration
            min={ 300 }
            max={ 3600 }
            label={ "Focus Duration: " }
            session={ session }
            setSession={ setSession }
          />
        </div>
        <div className="col">
          <div className="float-right">
            <BreakDuration
              min={ 60 }
              max={ 900 }
              label={ "Break Duration: " }
              session={ session }
              setSession={ setSession }
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className={classNames({
                "btn": true,
                "btn-primary": session.active,
                "btn-secondary": !session.active,
                "disabled": !session.active,
              })}
              title="Stop the session"
              onClick={stopButtonHandler}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <Countdown
        session={ session }
        isTimerRunning={ isTimerRunning }
      />
    </div>
  );
}

export default Pomodoro;
