import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import Duration from "./Duration";
// import the formatting utilities for duration
import { minutesToDuration, secondsToDuration } from "../utils/duration"

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  // set up state variable for focus (true) or break (false)
  const [focusSession, setFocusSession] = useState(true);

  // set up state variable for whether there is an active session
  const [activeSession, setActiveSession] = useState(false);

  // set up state variables for focus and breaks
  const [focusDuration, setFocusDuration] = useState(1500);
  const [breakDuration, setBreakDuration] = useState(300);

  // set up state variable for current timer
  const [timer, setTimer] = useState(focusDuration);


  useInterval(
    () => {
      console.log(timer)
      setTimer(timer - 1);

    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <Duration
            min={ 300 }
            max={ 3600 }
            label={ "Focus Duration: " }
            periodDuration={ focusDuration }
            setPeriodDuration={ setFocusDuration }
          />
        </div>
        <div className="col">
          <div className="float-right">
            <Duration
              min={ 60 }
              max={ 900 }
              label={ "Break Duration: " }
              periodDuration={ breakDuration }
              setPeriodDuration={ setBreakDuration }
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className={classNames({
                "btn": true,
                "btn-primary": activeSession,
                "btn-secondary": !activeSession,
              })}
              title="Stop the session"
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">Focusing for {secondsToDuration(timer)} minutes</h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(timer)} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={ (focusDuration - timer) / focusDuration * 100 }// TODO: Increase aria-valuenow as elapsed time increases
                style={{ width:`${ (focusDuration - timer) / focusDuration * 100}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
